"use client";

// src/use-divider.ts
import { useSeparator } from "@react-aria/separator";
import { divider } from "@nextui-org/theme";
import { useDOMRef } from "@nextui-org/react-utils";
import { useCallback, useMemo } from "react";
import { mergeProps } from "@react-aria/utils";
function useDivider(props) {
  const { ref, as, className, orientation, ...otherProps } = props;
  let Component = as || "hr";
  const domRef = useDOMRef(ref);
  if (Component === "hr" && orientation === "vertical") {
    Component = "div";
  }
  const { separatorProps } = useSeparator({
    elementType: typeof Component === "string" ? Component : "hr",
    orientation
  });
  const styles = useMemo(
    () => divider({
      orientation,
      className
    }),
    [orientation, className]
  );
  const getDividerProps = useCallback(
    (props2 = {}) => ({
      ref: domRef,
      className: styles,
      role: "separator",
      "data-orientation": orientation,
      ...mergeProps(separatorProps, otherProps, props2)
    }),
    [domRef, styles, orientation, separatorProps, otherProps]
  );
  return { Component, getDividerProps };
}

export {
  useDivider
};
